import FormInput from "../../components/form-input/form-input.component"
import { auth, signInWithGooglePopup , createUserDocumentFromAuth, signAutInWithEmailAndPassword} from "../../utils/firebase.utils"
import { useState } from "react"
import Button from "../../components/button/button.component"
import './sign-in-form.styles.scss'

const SignInForm = () => {
    const defautFormfield = {
        email: '',
        password: '',
    }

    const handleChange = (event) => {
        const {name,value} = event.target
        setFormField({...formFields, [name]:value})

    }

    const resetFormFields = () => {
        setFormField(defautFormfield)
       }

    const signInWithGoogle = async () => {
        const {user} = await signInWithGooglePopup();
         await createUserDocumentFromAuth(user)
    }

    const SignInWithEmailAndPassword = async (event) => {
        event.preventDefault();
        try {
            await signAutInWithEmailAndPassword(email, password);
            resetFormFields()
        } catch(error) {
            console.log('User or password not correct', error)
        }

    }

    const [formFields,setFormField]=useState(defautFormfield)
    const { email, password} = formFields

    return (
        <div className="sign-up-container">
        <h1>Sign In</h1>
        <h2>I already have an account</h2>
        <span>Sign in with your email and password</span>
        <form onSubmit={SignInWithEmailAndPassword}>
            <FormInput label="Email" type="text" required onChange={handleChange} name="email" value={email}/>
            <FormInput label="Password" type="text" required onChange={handleChange} name="password" value={password}/>
            <div className="buttons-container">
                <Button type="submit">SIGN IN</Button>
                <Button buttonType="google" onClick={signInWithGoogle}>SIGN IN WITH GOOGLE</Button>
            </div>
        </form>
        </div>

    )
}

export default SignInForm
