import FormInput from "../../components/form-input/form-input.component"
import { auth, signInWithGooglePopup , createUserDocumentFromAuth} from "../../utils/firebase.utils"
import { useState } from "react"
import Button from "../../components/button/button.component"

const SignInForm = () => {
    const defautFormfield = {
        email: '',
        password: '',
    }

    const handleChange = (event) => {
        const {name,value} = event.target
        setFormField({...formFields, [name]:value})

    }

    const logGoogleUser = async () => {
        const {user} = await signInWithGooglePopup();
        const userDocRef = await createUserDocumentFromAuth(user)
    }

    const logInWithEmailAndPassword = async (event) => {
        event.preventDefault();

    }

    const [formFields,setFormField]=useState(defautFormfield)
    const { email, password} = formFields

    return (
        <div>
        <h1>Sign In</h1>
        <h2>I already have an account</h2>
        <span>Sign in with your email and password</span>
        <form onSubmit={logInWithEmailAndPassword}>
            <FormInput label="Email" type="text" required onChange={handleChange} name="email" value={email}/>
            <FormInput label="Password" type="text" required onChange={handleChange} name="password" value={password}/>
            <Button type="submit">SIGN IN</Button>
            <Button type="google" onClick={logGoogleUser}>SIGN IN WITH GOOGLE</Button>
        </form>
        </div>

    )
}

export default SignInForm
